import javax.servlet.RequestDispatcher;
import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "CustomerServlet", urlPatterns = "/customer")
public class CustomerServlet extends HttpServlet {
    public static List<Customer> customerList = new ArrayList<>();

    @Override
    public void init() throws ServletException {
        customerList.add(new Customer("Mai Van Hoan", "1983/08/20", "Ha Noi", "data:image/jpeg;base64,"));
        customerList.add(new Customer("Nguyen Van Nam", "1990/02/12", "Bac Giang", "https://media.istockphoto.com/photos/smiling-friendly-young-woman-wearing-spectacles-picture-id1251308867?k=20&m=1251308867&s=612x612&w=0&h=lDj72q8pkc1m1vRTmESQy_zdZFSTiW7vWQyFH0a4_6k="));
        customerList.add(new Customer("Nguyen Thai Hoa", "1983/09/21", "Ho Chi Minh", "data:image/jpeg;base64,"));
        customerList.add(new Customer("Tran Dang Khoa", "1987/03/25", "Da Nang", "https://media.istockphoto.com/photos/smiling-friendly-young-woman-wearing-spectacles-picture-id1251308867?k=20&m=1251308867&s=612x612&w=0&h=lDj72q8pkc1m1vRTmESQy_zdZFSTiW7vWQyFH0a4_6k="));
        customerList.add(new Customer("Nguyen Dinh Thi", "1989/07/05", "Hung Yen", "data:image/jpeg;base64,"));
    }

    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }

    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        request.setAttribute("customerList",customerList);
        RequestDispatcher requestDispatcher = request.getRequestDispatcher("customer.jsp");
        requestDispatcher.forward(request, response);
    }
}
